import {
  Callout,
  CalloutAction,
  CalloutTitle,
  CalloutContent,
} from "@govtechmy/myds-react/callout";

export default function component() {
	return (// Basic - Uncontrolled Dismiss State
		<Callout
		  dismissible
		  onDismiss={() => {
		    console.log("MYDS: Dismissed event captured!");
		  }}
		>
		  <CalloutTitle>Title</CalloutTitle>
		  <CalloutContent>
		    A description to explain the success, warning, info or error event
		  </CalloutContent>
		  <CalloutAction>
		    <Button variant="default-outline">Call to Action</Button>
		  </CalloutAction>
		</Callout>

		// Action - Dismiss Callout via CalloutAction Render Prop
		<Callout
		  dismissible
		  onDismiss={() => {
		    alert("Action taken before callout gets dismissed");
		    console.log("MYDS: Dismissed event captured!");
		  }}
		>
		  <CalloutTitle>Title</CalloutTitle>
		  <CalloutContent>
		    A description to explain the success, warning, info or error event
		  </CalloutContent>
		  <CalloutAction>
		    <CalloutClose>
		      <Button variant="default-outline">Call to Action</Button>
		    </CalloutClose>
		  </CalloutAction>
		</Callout>
	);
}

